import React, { useState } from "react";

function Desktop() {
    const [isOver, setIsOver] = useState(false);
    const [droppedElements, setDroppedElements] = useState([]);

    const handleDragOver = (e) => {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'copy'; 
        setIsOver(true);
    };

    const handleDragLeave = (e) => {
        e.preventDefault();
        setIsOver(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsOver(false);
        const data = e.dataTransfer.getData('text/plain');

        // Create a new element object
        const newElement = {
            jsx: data,
            id: Date.now(), // Unique ID
            position: {
                x: e.clientX,
                y: e.clientY
            }
        };

        // Add to dropped elements
        setDroppedElements(prev => [...prev, newElement]);
    };

    return (
        <div 
            className={`desktop_div ${isOver ? 'hovered' : ''}`}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}

        >


            {/* Render dropped elements */}
            {droppedElements.map((element) => (
                <div
                    draggable
                    key={element.id}
                    style={{
                        position: 'absolute',
                        left: element.position.x,
                        top: element.position.y,
                        transform: `translate(${element.position.x}px,)`, // Center the element
                        zIndex: 10
                    }}
                    dangerouslySetInnerHTML={{ __html: element.jsx }}
                />
            ))}
        </div>
    );
}
export default Desktop;
