import React, { useState } from "react";

function Desktop() {
    const [isOver, setIsOver] = useState(false);
    const [droppedElements, setDroppedElements] = useState([]);

    const handleDragOver = (e) => {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'copy'; 
        setIsOver(true);
    };

    const handleDragLeave = (e) => {
        e.preventDefault();
        setIsOver(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsOver(false);
        const data = e.dataTransfer.getData('text/plain');

        // Create a new element object
        const newElement = {
            jsx: data,
            id: Date.now(), // Unique ID
            position: {
                x: e.clientX,
                y: e.clientY
            }
        };

        // Add to dropped elements
        setDroppedElements(prev => [...prev, newElement]);
    };

    return (
        <div 
            className={`desktop_div ${isOver ? 'hovered' : ''}`}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            style={{
                border: isOver ? '2px dashed #4CAF50' : '2px dashed #ccc',
                borderRadius: '8px',
                padding: '2rem',
                minHeight: '200px',
                position: 'relative',
                overflow: 'auto'
            }}
        >
            <h1>Drop Zone</h1>

            {/* Render dropped elements */}
            {droppedElements.map((element) => (
                <div
                    key={element.id}
                    style={{
                        position: 'absolute',
                        left: element.position.x,
                        top: element.position.y,
                        transform: 'translate(-50%, -50%)', // Center the element
                        zIndex: 10
                    }}
                    dangerouslySetInnerHTML={{ __html: element.jsx }}
                />
            ))}
        </div>
    );
}
export default Desktop;
