import React, { useEffect, useState } from "react";

function Contral_kayboard() {
    const [expanded, setExpanded] = useState(false);
    const [element,setelement]=useState()

    const handleDragStart = (e, text) => {
        e.dataTransfer.setData('text/plain', text);
        e.dataTransfer.effectAllowed = 'move';
    };
    useEffect( ()=>{
        const fechdata=async ()=>{
            const data=await fetch("/json/elements.json").then(res=>res.json())
            setelement(data.elements)
        }
        fechdata()

},[])

    return (
        <div className="contral_kayboard">
            <div 
                className="detiles" 
                style={{ 
                    maxWidth: expanded ? '260px' : '66px',
                    transition: 'max-width 0.3s ease'
                }}
            >
                <button 
                    className="expand-btn" 
                    onClick={() => setExpanded(!expanded)}
                >
                    {expanded ? 'Collapse' : 'Expand'}
                </button>

                <div className="inner-content" style={{
                    display: expanded ? 'flex' : 'none',
                    flexDirection: 'column',
                    gap: '8px'
                }}>
                    <li>
                        <button 
                            draggable
                            onDragStart={(e) => handleDragStart(e, element.j)}
                            style={{
                                backgroundColor: '#4CAF50',
                                color: 'white',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'move'
                            }}
                        >
                            mneue
                        </button>
                    </li>
                    <li>
                        <button 
                            draggable
                            onDragStart={(e) => handleDragStart(e, 'hi')}
                            style={{
                                backgroundColor: '#2196F3',
                                color: 'white',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'move'
                            }}
                        >
                            hi
                        </button>
                    </li>
                    <li>
                        <button 
                            draggable
                            onDragStart={(e) => handleDragStart(e, 'hie')}
                            style={{
                                backgroundColor: '#9C27B0',
                                color: 'white',
                                padding: '8px 16px',
                                borderRadius: '4px',
                                cursor: 'move'
                            }}
                        >
                            hie
                        </button>
                    </li>
                </div>
            </div>
        </div>
    );
}

export default Contral_kayboard;
