import React, { useState } from "react";

function Contral_kayboard() {
    const [expanded, setExpanded] = useState(false);

    return (
        <div className="contral_kayboard">
            <div 
                className="detiles" 
                style={{ 
                    maxWidth: expanded ? '260px' : '66px',
                    transition: 'max-width 0.3s ease'
                }}
            >
                <button 
                    className="expand-btn" 
                    onClick={() => setExpanded(!expanded)}
                >
                    {expanded ? 'Collapse' : 'Expand'}
                </button>
                <div className="inner-content" style={{}}>
                    <p>This is the inner content that will be revealed when expanded.</p>
                    <p>You can add any content here that will smoothly appear when the button is clicked.</p>
                </div>
            </div>
        </div>
    );
}

export default Contral_kayboard;
