import React, { useState } from "react";

function Desktop() {
    const [isOver, setIsOver] = useState(false);
    const [jsxCode, setJsxCode] = useState('');

    const handleDragOver = (e) => {
        e.preventDefault();
        e.dataTransfer.dropEffect = 'move';
        setIsOver(true);
    };

    const handleDragLeave = (e) => {
        e.preventDefault();
        setIsOver(false);
    };

    const handleDrop = (e) => {
        e.preventDefault();
        setIsOver(false);
        const data = e.dataTransfer.getData('text/plain');
        if (data === 'draggable-div') {
            const jsx = `
            <div
              style={{
                backgroundColor: '#4CAF50',
                width: '150px',
                height: '150px',
                borderRadius: '10px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontSize: '16px',
                fontWeight: 'bold'
              }}
            >
              Drag Me
            </div>
            `;
            setJsxCode(jsx);
        }
    };

    return (
        <div 
            className={`desktop_div ${isOver ? 'hovered' : ''}`}
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            style={{
                border: isOver ? '2px dashed #4CAF50' : '2px dashed #ccc',
                borderRadius: '8px',
                padding: '2rem',
                minHeight: '200px',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                justifyContent: 'center'
            }}
        >
            <h1>Drop Zone Area</h1>
            {jsxCode && (
                <div className="generated-code">
                    <h3>Generated JSX Code:</h3>
                    <pre className="code-block">
                        <code>{jsxCode}</code>
                    </pre>
                </div>
            )}
        </div>
    );
}
export default Desktop;
